INSERT INTO dw_process_definitions (id, function_unit_id, bpmn_xml, created_at, updated_at)
SELECT 10, f.id, '', NOW(), NOW()
FROM dw_function_units f WHERE f.code = 'fu-purchase-request'
ON CONFLICT (id) DO UPDATE SET 
    bpmn_xml = EXCLUDED.bpmn_xml,
    updated_at = NOW();

-- Reset sequence
SELECT setval('dw_process_definitions_id_seq', (SELECT MAX(id) FROM dw_process_definitions));